    private void initView() {
        viewpage = (ZNViewPager) findViewById(R.id.viewpage);
        tabLayout = (TabLayout) findViewById(R.id.tab_layout);

    }

    private void initTabLayout() {
        for (int i = 0; i < adapter.getCount(); i++) {
            View view = adapter.getPageTab(i);

            try {
                tabLayout.getTabAt(i).setCustomView(view);
            } catch (Exception e) {
                e.printStackTrace();
            }

        }
        tabLayout.setTabGravity(TabLayout.GRAVITY_FILL);
        tabLayout.setTabMode(TabLayout.MODE_FIXED);

        tabLayout.addOnTabSelectedListener(new TabLayout.OnTabSelectedListener() {
            @Override
            public void onTabSelected(TabLayout.Tab tab) {
                if (tab == null)
                    return;
                int position = tab.getPosition();
                viewpage.setCurrentItem(position, false);
                currentPage = position;
                if (position != 2) {
                    initTitle();
                } else {
                    setTitleVisibility(View.INVISIBLE);
                    setTitleViewClickable(false);
                    setRightDrawble(null, TitleBar2.DrawableIndex.RIGHT);
                    setRightBackgroudResource(R.drawable.icon_setting, PXUtils.dip2px(MainActivity.this, 27), PXUtils.dip2px(MainActivity.this, 27));
                    setRightOnClickListener(new View.OnClickListener() {
                        @Override
                        public void onClick(View v) {

                        }
                    });
                }


            }

            @Override
            public void onTabUnselected(TabLayout.Tab tab) {
                if (tab == null)
                    return;
                int position = tab.getPosition();
                if (adapter != null && position < adapter.getCount() && adapter.getCount() > 0 && adapter.getItem(position) != null) {
                    BaseFragment fragment = (BaseFragment) adapter.getItem(position);
                    if (fragment.isAlreadyOncreate()) {
                        fragment.onPageTurnBackground();
                    }
                }

            }

            @Override
            public void onTabReselected(TabLayout.Tab tab) {

            }
        });

    }

