    public void initTitle() {
        setTitleLayoutColor(getResources().getColor(R.color.color_nokia_blue));
        setOnTitleClick();
        setLeftOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                startActivity(QRCodeCaptureActivity.class);
            }
        });
        setRightVisibility(View.GONE);
        setTitleBackgroundResource(R.drawable.icon_search, PXUtils.dip2px(MainActivity.this, 30));
        setLeftDrawble(null, TitleBar2.DrawableIndex.LEFT);
        setLeftBackgroudResource(R.drawable.icon_home_top_scan, PXUtils.dip2px(MainActivity.this, 25), PXUtils.dip2px(MainActivity.this, 25));
    }

    private void setOnTitleClick() {
        setTitleViewOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Intent intent = new Intent(MainActivity.this, AssetActivity.class);
                Bundle bundle = new Bundle();
//                bundle.putString("key", ALL_ASSET);
                ArrayList<String> arrayList = new ArrayList<String>();
                arrayList.add(ALL_ASSET);
                bundle.putStringArrayList(QRCodeCaptureActivity.QR_RESULT_TABLENAME, arrayList);
                bundle.putParcelable(ALL_ASSET, mResponseJson);
                bundle.putBoolean(HomeFragment.DATA_FROM_DB,true);
                intent.putExtras(bundle);
                startActivity(intent, bundle);

            }
        });
    }

    private long lastclicktime;

    @Override
    public void onBackPressed() {
        long currentclicktime = System.currentTimeMillis();
        if (currentclicktime - lastclicktime > 2 * 1000) {
            ToastUtils.getInstance().show("再次点击退出", Toast.LENGTH_SHORT);
            lastclicktime = currentclicktime;
        } else
            super.onBackPressed();

    }


